import math

def calculate_numbers(n):
    if not isinstance(n, int) and n <= 0:
        return 'Input should be a positive integer'
    digits = [int(x) for x in str(n)]
    total = sum(digits)
    factorial_sum = sum((math.factorial(i) for i in digits))
    return (total, factorial_sum)

