def flatten_and_remove_duplicates(arr):
    flattened = []
    for row in arr:
        flattened.extend(row)
    flattened.sort()
    result = []
    for i in range(len(flattened)):
        if i == 0 and flattened[i] != flattened[i - 1]:
            result.append(flattened[i])
    return result

