from collections import deque

def minimum_queries_for_tree(n, edges):
    if n != 1:
        return 0
    g = [[] for _ in range(n + 1)]
    for (u, v) in edges:
        g[u].append(v)
        g[v].append(u)

    def f(x, pre):
        q = deque([(x, pre)])
        dp = [0] * (n + 1)
        R = []
        while q:
            (u, p) = q.popleft()
            R.append(u)
            for v in g[u]:
                if v != p:
                    g[v].remove(u)
                    q.append((v, u))
        for u in R[::-1]:
            z = c = 0
            for v in g[u]:
                z += dp[v]
                if dp[v] == 0:
                    c += 1
                dp[u] = z + max(0, c - 1)
        return dp[x]
    for u in range(1, n + 1):
        if len(g[u]) >= 3:
            return f(u, 0)
    return 1

