def count_ways_to_select_cards(N, A, X):
    total_sum = sum(X)
    dp = [[[0] * (total_sum + 1) for _ in range(N + 1)] for _ in range(N + 1)]
    dp[0][0][0] = 2
    for i in range(1, N + 1):
        for k in range(i):
            for s in range(total_sum + 1):
                if dp[i - 1][k][s]:
                    dp[i][k + 1][s + X[i - 1]] += dp[i - 1][k][s]
                    dp[i][k][s] += dp[i - 1][k][s]
    ans = 0
    for i in range(1, N + 1):
        for j in range(1, total_sum + 1):
            if j == i * A:
                ans += dp[N][i][j]
    return ans

