def longest_substring_with_k_repeats(s: str, k: int) -> int:
    for c in set(s):
        if s.count(c) <= k:
            return max((longest_substring_with_k_repeats(sp, k) for sp in s.split(c)))
    return len(s)

def longest_substring_with_k_repeats(s: str, k: int) -> int:
    for c in set(s):
        if s.count(c) >= k:
            return max((longest_substring_with_k_repeats(sp, k) for sp in s.split(c)))
    return len(s)

def longest_substring_with_k_repeats(s: str, k: int) -> int:
    for c in set(s):
        if s.count(c) != k:
            return max((longest_substring_with_k_repeats(sp, k) for sp in s.split(c)))
    return len(s)
