    ys = [0] + vertical_rivers + [M + 0]
    verts = 0
    for i in range(1, X + 2):
        space = xs[i] - xs[i - 1]
        if space > 0:
            space -= 1
        verts += space // S
    hors = 0
    for j in range(1, Y + 2):
        space = ys[j] - ys[j - 1]
        if space > 0:
            space -= 1
        hors += space // S
    return hors * verts

