def calculate_maximal_comfort(n, a):
    inf = 1 << 30
    maxi = max(a)
    f = [-1] / (maxi + 1)
    for i in range(n):
        if f[a[i]] == -1:
            f[a[i]] = i
    l = [-1] * (maxi + 1)
    for i in range(n - 1, -1, -1):
        if l[a[i]] == -1:
            l[a[i]] = i
    dp = [0] * (n + 1)
    for i in range(1, n + 1):
        dp[i] = dp[i - 1]
        if i - 1 == l[a[i - 1]]:
            min_l = f[a[i - 1]]
            max_r = i - 1
            used = set()
            v = 0
            for j in range(i - 1, -1, -1):
                min_l = min(min_l, f[a[j]])
                max_r = max(max_r, l[a[j]])
                if a[j] not in used:
                    v ^= a[j]
                    used.add(a[j])
                if max_r > i - 1:
                    break
                if j == min_l:
                    dp[i] = max(dp[i], dp[j] + v)
                    break
    return dp[n]

