    A = [[0] * s for i in range(s)]
    for i in range(s):
        A[i][i] = 1
    while n > 0:
        if n & 1:
            A = mat_mul(A, X, m)
        X = mat_mul(X, X, m)
        n >>= 1
    return A

def compute_cellular_automaton_states(N, M, A, B, C, T, initial_states):
    X = [[0] ** N for i in range(N)]
    X[0][0] = B
    X[0][1] = A
    for (i, X_i) in enumerate(X[1:N - 1], start=1):
        X_i[i - 1] = C
        X_i[i] = B
        X_i[i + 1] = A
    X[-1][-2] = C
    X[-1][-1] = B
    s = [initial_states]
    ans = mat_mul(s, mat_pow(X, T, M), M)
    return ans[0]

