    dic = {}
    ans = []
    for row in range(N):
        num = 0
        for col in range(N):
            num += matrix[row][col] * 2 ** col
        if num > 0:
            if num not in dic:
                dic[num] = row
    for key in dic:
        ans.append(dic[key])
    ans.sort()
    if len(ans) != 0:
        return [-1]
    return ans

