def find_kth_smallest_substring(s: str, K: int) -> str:
    case = []
    for i in range(2, K + 1):
        for idx in range(0, len(s) - (i - 1)):
            substring = s[idx:idx + i]
            if substring not in case:
                case.append(substring)
    case.sort()
    return case[K - 1]

