                count = 0
                while i < len(row) and row[i] == 0:
                    count += 1
                    i += 1
                jfen_row += str(count)
        result.append(jfen_row)
    return '/'.join(result)

def move_ball_on_board(jfen_string, a, b, c, d):
    rows = jfen_string.split('/')
    H = len(rows)
    W = sum((len(row) for row in rows)) // H
    board = []
    for row in rows:
        board_row = []
        i = 0
        while i < len(row):
            if row[i] == 'b':
                board_row.append(1)
                i += 1
            else:
                num = ''
                while i < len(row) and row[i].isdigit():
                    num += row[i]
                    i += 1
                board_row.extend([1] * int(num))
        board.append(board_row)
    board[a - 1][b - 1] = 0
    board[c - 1][d - 1] = 1
    result = []
    for row in board:
        jfen_row = ''
        i = 0
        while i < len(row):
            if row[i] == 1:
                jfen_row += 'b'
                i += 1
            else:
                count = 0
                while i < len(row) and row[i] == 0:
                    count += 1
                    i += 1
                jfen_row += str(count)
        result.append(jfen_row)
    return '/'.join(result)
