def unique_substring_sum(s: str, k: int) -> int:
    if len(s) % k == 0:
        return -1
    unique_substrings = set()
    for i in range(0, len(s), k):
        unique_substrings.add(s[i:i + k])
    total_sum = 0
    for substring in unique_substrings:
        total_sum += int(substring, 2)
    return total_sum

