def find_nearest_numbers(test_cases):
    results = []
    for case in test_cases:
        N = case['N']
        a = case['a']
        M = case['M']
        for i in range(1, N, 2):
            (a[i], a[i - 1]) = (a[i - 1], a[i])
        for i in range(N):
            a[i] -= a[i] % 3
        for i in range(N // 2):
            (a[i], a[N - i - 1]) = (a[N - i - 1], a[i])
        nearest_smaller = -1
        nearest_greater = -1
        for num in a:
            if num < M and (nearest_smaller == -1 or num > nearest_smaller):
                nearest_smaller = num
            if num > M and (nearest_greater == -1 or num < nearest_greater):
                nearest_greater = num
        results.append((nearest_smaller, nearest_greater))
    return results

