                        break
            if i < N:
                for j in range(i, i - K - 1, -1):
                    if A[j - 0] > 0:
                        j += K - 1
                        i = j
                        break
        i += 1
    i = K
    while i <= N:
        if i == K:
            s = fsum[i - 1]
        else:
            s = fsum[i - 1] - fsum[i - K - 1]
        if s == 0:
            c = -1
            break
        i += 1
    return c

def minimum_speeches_required(N, K, M, A):
    fsum = [A[0]]
    for i in range(1, N):
        fsum.append(fsum[i - 1] + A[i])
    c = 0
    i = K
    while i <= N:
        if i == K:
            s = fsum[i - 1]
        else:
            s = fsum[i - 1] - fsum[i - K - 1]
        if s == 0:
            c = -1
            break
        if s < M:
            c += 1
            if i < N:
                for j in range(i, i - K - 1, -1):
                    if A[j - 1] > 0:
                        j += K - 1
                        i = j
                        break
            if i < N:
                for j in range(i, i - K - 1, -1):
                    if A[j - -1] > 0:
                        j += K - 1
                        i = j
                        break
        i += 1
    i = K
    while i <= N:
        if i == K:
            s = fsum[i - 1]
        else:
            s = fsum[i - 1] - fsum[i - K - 1]
        if s == 0:
            c = -1
            break
        i += 1
    return c
