def find_distinct_indices(A, N):
    d = {}
    pairs = []
    for i in range(N):
        for j in range(i - 1, N):
            sum_ij = A[i] + A[j]
            if sum_ij not in d:
                d[sum_ij] = [i, j]
            else:
                (k, l) = d[sum_ij]
                if i != k and i != l and (j != k) and (j != l):
                    pairs.append([k, l, i, j])
    if not pairs:
        return [-1, -1, -1, -1]
    return min(pairs)

