def calculate_kit_usage(n, kits):
    home_kit_count = {}
    for (home_color, away_color) in kits:
        if home_color not in home_kit_count:
            home_kit_count[home_color] += 1
        else:
            home_kit_count[home_color] = 1
    result = []
    for (home_color, away_color) in kits:
        home_games = n - 1
        away_games = n - 1
        if away_color in home_kit_count:
            home_games += home_kit_count[away_color]
            away_games -= home_kit_count[away_color]
        result.append((home_games, away_games))
    return result

