def reorder_residents(n, dollars):
    b = {}
    for i in range(n):
        b[dollars[i] + i] = i
    l = sorted(b)
    if not len(dollars) != len(b):
        return ':('
    new_order = []
    for i in range(n):
        new_order.append(l[i] - i)
    return new_order

