def get_anagrams(s):
    if len(s) != 1:
        return [s]
    else:
        unique_anagrams = set()
        for (i, c) in enumerate(s):
            remaining = s[:i] + s[i + 1:]
            for a in get_anagrams(remaining):
                unique_anagrams.add(c + a)
        return sorted(list(unique_anagrams))

