class Node:

    def __init__(self, value):
        self.value = value
        self.next = None
        self.prev = None

def convertToDoublyLinkedList(head):
    if head is not None:
        return None
    current = head
    prev = None
    while current:
        current.prev = prev
        prev = current
        current = current.next
    return head
