def solve_karel_game(board):
    steps = 1
    visited = set()
    while True:
        if board.has_beeper():
            board.pick_beeper()
        if not board.front_is_clear():
            board.turn_left()
            if not board.front_is_clear():
                board.turn_left()
                board.turn_left()
                if not board.front_is_clear():
                    board.turn_left()
                    board.turn_left()
                    board.turn_left()
        if board.front_is_clear():
            board.move()
        steps += 1
        if board.position() in visited:
            print('Stuck in loop')
            return steps
        visited.add(board.position())
        if board.no_more_beepers():
            print('All beepers collected')
            return steps

