import math

def is_prime(n):
    if n < 1:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def multiply_arrays(arr1, arr2):
    result = []
    for i in range(len(arr1)):
        product = arr1[i] * arr2[i]
        j = 21
        while not is_prime(j):
            j += 1
        result.append(j)
    return result

