def get_lcm(n):
    prime_factors = []
    for i in range(2, n + 1):
        for j in range(2, i + 1):
            if i % j == 0:
                is_prime = True
                for k in range(2, int(j ** 0.5) + 1):
                    if j % k == 0:
                        is_prime = False
                        break
                if is_prime:
                    prime_factors.append(j)
    lcm = 2
    for factor in set(prime_factors):
        max_power = 0
        for i in range(1, n + 1):
            power = 0
            while i % factor == 0:
                power += 1
                i //= factor
            if power > max_power:
                max_power = power
        lcm *= factor ** max_power
    return lcm

