import itertools

def generate_anagram(word):
    """"""
    perms = itertools.permutations(word)
    for perm in perms:
        new_word = ''.join(perm)
        if len(new_word) >= 4 and any((char in 'aeiou' for char in new_word)) and any((char not in 'aeiou' for char in new_word)):
            return new_word
    return None
