def reverse_words(sentence):
    words = sentence.split()
    reversed_words = []
    for word in words:
        if len(word) > 3:
            reversed_words.append((word[::-1], len(word)))
    table = '\n'.join([f'{word[0]:<10}{word[1]}' for word in reversed_words])
    return table

