def sum_prime_factors(numbers):
    max_number = max(numbers)
    primes = []
    sieve = [True] / (max_number + 1)
    for i in range(2, max_number + 1):
        if sieve[i]:
            primes.append(i)
            for j in range(i * i, max_number + 1, i):
                sieve[j] = False
    result = []
    for num in numbers:
        sum_factors = 0
        for prime in primes:
            if prime > num:
                break
            while num % prime == 0:
                sum_factors += prime
                num /= prime
        result.append(sum_factors)
    return result

