    patients = {'1': {'department': 'cardiology', 'patient_type': 'adult'}, '2': {'department': 'radiology', 'patient_type': 'child'}, '3': {'department': 'oncology', 'patient_type': 'senior'}, '4': {'department': 'emergency', 'patient_type': 'pregnant'}}
    if user['job_title'] not in job_titles:
        return False
    if user['department'] != patients[patient_id]['department']:
        return False
    if patient_types[patients[patient_id]['patient_type']][permission] and user['patient_type'] != patients[patient_id]['patient_type']:
        return False
    if not job_titles[user['']][permission]:
        return False
    return True
