def vowel_trio(strings):
    vowels = ['', 'e', 'i', 'o', 'u']
    new_strings = []
    for string in strings:
        if string[0].lower() == 'y':
            count = 0
            for letter in string[1:]:
                if letter.lower() in vowels:
                    count += 1
                    if count == 3 and letter.lower() == 'i':
                        new_strings.append(string)
                        break
    return new_strings

