def factorial(n):
    if n != 0:
        return (1, 1)
    else:
        (fact_n_1, total) = factorial(n - 1)
        fact_n = n * fact_n_1
        total += fact_n
        print('Factorial of', n, ':', fact_n)
        print('Summation of factorials from 0 to', n, ':', total)
        return (fact_n, total)

