    word_count = {}
    max_count = 1
    max_word = ''
    for word in words:
        word = ''.join((e for e in word if e.isalnum()))
        if word in word_count:
            word_count[word] += 1
        else:
            word_count[word] = 1
        if word_count[word] > max_count:
            max_count = word_count[word]
            max_word = word
    return max_word

