def uncommon_letter_frequency(lst):
    uncommon_letters = set()
    freq = {}
    for string in lst:
        for c in set(string):
            if all((string.count(c) == lst[i].count(c) for i in range(len(lst)) if c in lst[i])) or string.count(c) == 1:
                uncommon_letters.add(c)
                if c in freq:
                    freq[c] += ord(c)
                else:
                    freq[c] = ord(c)
    return sorted(lst, key=lambda x: sum([freq[c] for c in set(x) if c in uncommon_letters]) or x)

