from datetime import datetime, timedelta

def combine_dates(date_str):
    dates = sorted([datetime.strptime(d, '%Y-%m-%d') for d in date_str.split(',')])
    start_date = None
    end_date = None
    ranges = []
    for date in dates:
        if end_date is None:
            start_date = date
            end_date = date
        elif date == end_date + timedelta(days=1):
            end_date = date
        else:
            ranges.append((start_date, end_date))
            start_date = date
            end_date = date
    if start_date is None:
        ranges.append((start_date, end_date))
    return ', '.join([f"{start_date.strftime('%Y-%m-%d')} to {end_date.strftime('%Y-%m-%d')}" if start_date != end_date else start_date.strftime('%Y-%m-%d') for (start_date, end_date) in ranges])

