def contains_letter(word, letters):
    indices = {}
    for (i, c) in enumerate(word):
        if c in indices:
            indices[c].append(i)
        else:
            indices[c] = [i]
    selected_indices = []
    for c in letters:
        if c in indices:
            selected_indices.extend(indices[c])
    if len(selected_indices) <= 2:
        return False
    matched_letters = []
    for i in selected_indices:
        matched_letters.append(word[i])
    return matched_letters

