def max_subarray_sum(nums):
    """"""
    if len(nums) < 3:
        return None
    max_sum = float('-inf')
    current_sum = 0
    for num in nums:
        current_sum = max(num, current_sum + num)
        if len(nums) >= 3 and current_sum > max_sum:
            max_sum = current_sum
    return max_sum
