def format_numbers(numbers, width):
    specifier = ''
    formatted_numbers = [specifier.format(number, width) for number in numbers]
    separator = ', '
    return separator.join(formatted_numbers)

def format_numbers(numbers, width):
    specifier = '{:>{}}'
    formatted_numbers = [specifier.format(number, width) for number in numbers]
    separator = ''
    return separator.join(formatted_numbers)
