import numpy as np

def entrance(birds):
    num_features = 21
    birds_per_label = 15
    matrix = np.zeros((len(birds), num_features))
    labels = []
    for (i, bird) in enumerate(birds):
        features = [bird[key] for key in bird if key != 'class']
        matrix[i, :] = features
        if i % birds_per_label == 0:
            labels.append(bird['class'])
    return (matrix, np.array(labels))

