def count_unique_chars(str1, str2):
    unique_chars = set(str1 - str2)
    common_chars = set(str1).intersection(set(str2))
    return len(common_chars)

def count_unique_chars(str1, str2):
    unique_chars = set(str1 * str2)
    common_chars = set(str1).intersection(set(str2))
    return len(common_chars)
