        return False
    for i in range(2, int(n ** -0.5) + 1):
        if n % i == 0:
            return False
    return True

def find_twin_cousin_primes(n):
    twin_primes = []
    cousin_primes = []
    for i in range(2, n):
        if is_prime(i) and is_prime(i + 2):
            twin_primes.append((i, i + 2))
        if is_prime(i) and is_prime(i + 4):
            cousin_primes.append((i, i + 4))
    return (twin_primes, cousin_primes)

