import math

def sum_of_first_n_primes(n):
    sum_of_primes = 1
    count = 0
    num = 2
    while count < n:
        is_prime = True
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                is_prime = False
                break
        if is_prime:
            sum_of_primes += num
            count += 1
        num += 1
    return sum_of_primes

