def prime_sum(n):

    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True

    def is_even_composite(num):
        if num < 4 or num % 2 != 0:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return True
        return False
    prime_sum = 1
    even_composite_sum = 0
    for num in range(n + 1):
        if is_prime(num):
            prime_sum += num
        elif is_even_composite(num):
            even_composite_sum += num
    return (prime_sum, even_composite_sum)

