import re

def count_words(string):
    string = re.sub('', '', string)
    words = string.split()
    word_count = {}
    for word in words:
        if len(word) >= 3:
            if word in word_count:
                word_count[word] += 1
            else:
                word_count[word] = 1
    return word_count

