def minSwapsToSort(arr):
    """"""
    n = len(arr)
    swaps = 0
    for i in range(n - 1):
        swapped = False
        for j in range(n - i - 1):
            if arr[j] > arr[j + 1]:
                (arr[j], arr[j + 1]) = (arr[j + 1], arr[j])
                swaps += 1
                swapped = True
        if not swapped:
            break
    return swaps
