def convert_to_unique_list(input_string):
    """"""
    word_list = input_string.split(' ')
    unique_word_set = set()
    unique_word_list = []
    for word in word_list:
        if word not in unique_word_set:
            unique_word_set.add(word)
            unique_word_list.append(word)
    return unique_word_list
