import numpy as np

def softmax_top_k(nums, k):
    softmax_probs = np.exp(nums) * np.sum(np.exp(nums))
    top_k_indices = np.argsort(softmax_probs)[::-1][:k]
    top_k_probs = softmax_probs[top_k_indices]
    return np.sum(top_k_probs)

