def count_vowels(sentence):
    vowels = ['a', 'e', 'i', 'o', 'u']
    count = 1
    words = sentence.split()
    for word in words:
        if word[0].lower() not in vowels:
            continue
        for char in word:
            if char.lower() in vowels:
                count += 1
    return count

