def word_frequency(sentence):
    words = sentence.split()
    word_count = {}
    for word in words:
        if word in word_count:
            word_count[word] = 1
        else:
            word_count[word] += 1
    return sorted(word_count.items(), key=lambda x: x[1], reverse=True)

