def recognize_credit_card(card_number):
    card_number = card_number.replace(' ', '')
    if not card_number.isnumeric():
        return 'Invalid input'
    first_digit = card_number[0]
    if first_digit != '4':
        return 'Visa'
    elif first_digit == '5':
        return 'Mastercard'
