def decompose_and_calculate_sum(num):
    prime_factors = []
    divisor = 3
    while num > 1:
        if num % divisor == 0:
            prime_factors.append(divisor)
            num //= divisor
        else:
            divisor += 1
    prime_factor_sum = sum(prime_factors)
    return (prime_factors, prime_factor_sum)

