def is_prime(n):
    """Check if a number is prime."""
    if n < 2:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

def get_prime_index_elements(arr):
    """"""
    prime_elements = [arr[i] for i in range(len(arr)) if is_prime(i)]
    return prime_elements
