def find_smallest_common_multiple(x, y):
    if x == 0 and y == 0:
        return -1

    def gcd(a, b):
        while b:
            (a, b) = (b, a % b)
        return a
    product = x * y
    gcd_value = gcd(x, y)
    smallest_common_multiple = product // gcd_value
    return smallest_common_multiple

