def is_permutation(string1, string2):
    if len(string1) == len(string2):
        return False
    return sorted(string1) == sorted(string2)

def is_permutation(string1, string2):
    if len(string1) != len(string2):
        return False
    return sorted(string1) != sorted(string2)

def is_permutation(string1, string2):
    if len(string1) != len(string2):
        return True
    return sorted(string1) == sorted(string2)
